	, FAX_NO
	, ADD_NO
	, NEIGHBOR
	, CREATION_DATE	
)VALUES (
	  #{ ptnCd }
	, #{ bizrRegNo }
	, #{ ptnNm }
	, #{ rpstNm }
	, #{ rpstTel }
	, #{ faxNo }
	, #{ addNo }
	, #{ neighbor }
	, SYSDATE		
	)

mapper.select:

SELECT PTN_NM, NEIGHBOR
FROM PTN_INFOS
WHERE PTN_CD = #{ ptnCd }
